import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";

export default class Tutorial extends Component {
  constructor(props) {
    super(props);
    this.onChangeProductName = this.onChangeProductName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeProductColors = this.onChangeProductColors.bind(this);
    this.onChangeProductPrice = this.onChangeProductPrice.bind(this);
    this.onChangeProductBrand = this.onChangeProductBrand.bind(this);
    this.onCHangeProductImagePath = this.onChangeProductImagePath.bind(this);
    this.getTutorial = this.getTutorial.bind(this);
    this.updatePublished = this.updatePublished.bind(this);
    this.updateTutorial = this.updateTutorial.bind(this);
    this.deleteTutorial = this.deleteTutorial.bind(this);

    this.state = {
      currentTutorial: {
        id: null,
        productName: "",
        description: "",
        productColors: "",
        productPrice: 0, 
        productBrand: "",
        productImagePath: "",
        published: false,
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getTutorial(this.props.match.params.id);
  }

  onChangeProductName(e) {
    const name = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          productName: name
        }
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        description: description
      }
    }));
  }

  onChangeProductColors(e) {
    this.setState({
      productColors: e.target.value
    });
  }

  onChangeProductPrice(e) {
    this.setState({
      productPrice: e.target.value
    });
  }

  onChangeProductBrand(e) {
    this.setState({
      productBrand: e.target.value
    });
  }

  onChangeProductImagePath(e) {
    this.setState({
      productImagePath: e.target.value
    });
  }

  getTutorial(id) {
    TutorialDataService.get(id)
      .then(response => {
        this.setState({
          currentTutorial: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updatePublished(status) {
    var data = {
      id: this.state.currentTutorial.id,
      title: this.state.currentTutorial.title,
      description: this.state.currentTutorial.description,
      published: status
    };

    TutorialDataService.update(this.state.currentTutorial.id, data)
      .then(response => {
        this.setState(prevState => ({
          currentTutorial: {
            ...prevState.currentTutorial,
            published: status
          }
        }));
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateTutorial() {
    TutorialDataService.update(
      this.state.currentTutorial.id,
      this.state.currentTutorial
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The tutorial was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteTutorial() {    
    TutorialDataService.delete(this.state.currentTutorial.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/tutorials')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentTutorial } = this.state;

    return (
      <div>
        {currentTutorial ? (
          <div className="edit-form">
            <h4>Product Detail</h4>
            <form>
              <div className="form-group">
                <label htmlFor="title">Product Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="Product Name"
                  value={currentTutorial.productName}
                  onChange={this.onChangeTitle}
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentTutorial.description}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
              <label htmlFor="price"> Price</label>
              <input
                type="text"
                className="form-control"
                id="price"
                required
                value={this.state.productPrice}
                onChange={this.onChangeProductPrice}
                name="price"
              />
            </div>

            <div className="form-group">
              <label htmlFor="colors"> Colors</label>
              <input
                type="text"
                className="form-control"
                id="colors"
                required
                value={this.state.productColors}
                onChange={this.onChangeProductColors}
                name="colors"
              />
            </div>

            <div className="form-group">
              <label htmlFor="brand"> Brand</label>
              <input
                type="text"
                className="form-control"
                id="brand"
                required
                value={this.state.productBrand}
                onChange={this.onChangeProductBrand}
                name="brand"
              />
            </div>

            <div className="form-group">
              <label htmlFor="imagepath"> ImagePath</label>
              <input
                type="text"
                className="form-control"
                id="imagepath"
                required
                value={this.state.productImagePath}
                onChange={this.onChangeProductImagePath}
                name="imagepath"
              />
            </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentTutorial.published ? "Published" : "Pending"}
              </div>
            </form>

            {currentTutorial.published ? (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updatePublished(false)}
              >
                UnPublish
              </button>
            ) : (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updatePublished(true)}
              >
                Publish
              </button>
            )}

            <button
              className="badge badge-danger mr-2"
              onClick={this.deleteTutorial}
            >
              Delete
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updateTutorial}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Tutorial...</p>
          </div>
        )}
      </div>
    );
  }
}
